class InputExample(object):
    """A single training/test example for simple sequence classification."""

    def __init__(self, guid, text_a, text_b=None, label=None):
        """Constructs a InputExample.
        Args:
            guid: Unique id for the example.
            text_a: string. The untokenized text of the first sequence. For single
            sequence tasks, only this sequence must be specified.
            text_b: (Optional) string. The untokenized text of the second sequence.
            Only must be specified for sequence pair tasks.
            label: (Optional) string. The label of the example. This should be
            specified for train and dev examples, but not for test examples.
        """
        self.guid = guid
        self.text_a = text_a
        self.text_b = text_b
        self.label = label

class InputFeatures(object):
    """A single set of features of data."""

    def __init__(self, input_ids, input_mask, segment_ids, label_id):
        self.input_ids = input_ids
        self.input_mask = input_mask
        self.segment_ids = segment_ids
        self.label_id = label_id

class DataProcessor(object):
    """Interface for sequence classification."""

    def get_train_examples(self, data_dir):
        """Gets a collection of `InputExample`s for the train set."""
        raise NotImplementedError()

    def get_dev_examples(self, data_dir):
        """Gets a collection of `InputExample`s for the dev set."""
        raise NotImplementedError()

    def get_labels(self):
        """Gets the list of labels for this data set."""
        raise NotImplementedError()

class GLUEDataProcessor(DataProcessor): 
    """Base class for data converters for GLUE sequence classification data sets."""

    @classmethod
    def _read_tsv(cls, input_file, quotechar=None):
        """Reads a tab separated value file."""
        with open(input_file, "r") as f:
            reader = csv.reader(f, delimiter="\t", quotechar=quotechar)
            lines = []
            for line in reader:
                if sys.version_info[0] == 2:
                    line = list(unicode(cell, 'utf-8') for cell in line)
                lines.append(line)
            return lines

class DeepMojiDataProcessor(DataProcessor):
    '''This dataset is small enough that we keep it in memory'''
    
    def __init__(self, num_labels):
        super(DeepMojiDataProcessor, self).__init__()
        self.num_labels = num_labels
            
    def _create_examples(self, split, raw_data):
        examples = []
        for i, j in enumerate(raw_data[f'{split}_ind']):
            guid = f'{split}-{i}'
            text = raw_data['texts'][j]
            label = np.argmax(raw_data['info'][j]['label'])
            example = InputExample(guid=guid, text_a=text, label=label)
            examples.append(example)
        return examples
    
    def get_train_examples(self, data_dir):
        with open(os.path.join(data_dir, 'raw.pickle'), 'rb') as f:
            raw_data = pickle.load(f, encoding="latin1")
        return self._create_examples('train', raw_data)

    def get_dev_examples(self, data_dir):
        with open(os.path.join(data_dir, 'raw.pickle'), 'rb') as f:
            raw_data = pickle.load(f, encoding="latin1")
        return self._create_examples('val', raw_data)
            
    def get_test_examples(self, data_dir):
        with open(os.path.join(data_dir, 'raw.pickle'), 'rb') as f:
            raw_data = pickle.load(f, encoding="latin1")
        return self._create_examples('test', raw_data)

    def get_labels(self):
        '''For example, for PsychExp, according to https://github.com/TetsumichiUmada/text2emoji, 
        the labels are ["joy", "fear", "anger", "sadness", "disgust", "shame", "guilt"]'''
        return np.arange(self.num_labels)

class PsychExpProcessor(DeepMojiDataProcessor):
    def __init__(self, pickle_file='data/PsychExp'):
        super(PsychExpProcessor, self).__init__(num_labels=7)

class MrpcProcessor(GLUEDataProcessor):
    """Processor for the MRPC data set (GLUE version)."""

    def get_train_examples(self, data_dir):
        """See base class."""
        return self._create_examples(
            self._read_tsv(os.path.join(data_dir, "train.tsv")), "train")

    def get_dev_examples(self, data_dir):
        """See base class."""
        return self._create_examples(
            self._read_tsv(os.path.join(data_dir, "dev.tsv")), "dev")

    def get_labels(self):
        """See base class."""
        return ["0", "1"]

    def _create_examples(self, lines, set_type):
        """Creates examples for the training and dev sets."""
        examples = []
        for (i, line) in enumerate(lines):
            if i == 0:
                continue
            guid = "%s-%s" % (set_type, i)
            text_a = line[3]
            text_b = line[4]
            label = line[0]
            examples.append(
                InputExample(guid=guid, text_a=text_a, text_b=text_b, label=label))
        return examples


